xsl::xstringtransform xst( "stylesheet.xsl" );
xst << xml::start( "root" )
        << xml::start( "element" )
        << xml::end()
    << xml::end();

xsl::xstringtransform xst( "stylesheet.xsl" );
xml::xistringstream xis(
  "<root>"
    "<element/>"
  "</root>"
);
xst << xis;

xsl::xstringtransform xst( "final-stylesheet.xsl" );
xsl::xbuffertransform xbt( "first-stylesheet.xsl" );
xml::xistringstream xis( "<root/>" );
xst << xbt << xis;

xsl::xbuffertransform xbt( "first-stylesheet.xsl" );
xsl::xstringtransform xst( "final-stylesheet.xsl" );
xml::xistringstream xis( "<root/>" );
xst << xml::start( "root" )
      << xbt << xis
      << xbt << xis
    << xml::end();

xsl::xstringtransform xf( "stylesheet.xsl" );
xml::xistringstream xis( "<root/>" );
xf << xsl::parameter( "mykey", "expression" )
   << xis;

xsl::xbuffertransform xbt( "first-stylesheet.xsl" );
xsl::xstringtransform xst( "final-stylesheet.xsl" );
xml::xistringstream xis( "<root/>" );
xst << xbt << xsl::parameter( "key", "expression" )
           << xis;
