#include <xeumeuleu/xml.h>
#include <string>

std::string name, content;
xml::xifstream xis( "my_document.xml" );
xis >> xml::start( "document" )
      >> xml::start( "element" )
        >> xml::attribute( "name", name )
      >> xml::end()
      >> xml::content( "another-element", content );
